    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String name = jTextField1.getText();
        String city = jTextField2.getText();
        String contact = jTextField3.getText();
        char[] password = jPasswordField1.getPassword();
        String pass = String.valueOf(password);
        String address = jTextArea2.getText();
        Police newPolice = new Police(name,contact,address,city,pass);
        int policeID = Police.getID();
        JOptionPane.showMessageDialog(this, "Police ID is "+policeID);
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

