    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int pID = Integer.parseInt(jTextField1.getText());
        Police.deleteRecord(pID);
        JOptionPane.showMessageDialog(this, "Record Deleted");
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        this.policeID = Integer.parseInt(jTextField1.getText());
        jLabel5.setVisible(true);
        jLabel6.setVisible(true);
        jLabel8.setVisible(true);
        jLabel9.setVisible(true);
        jTextField2.setVisible(true);
        jTextField3.setVisible(true);
        jTextField4.setVisible(true);
        jTextField5.setVisible(true);
        jScrollPane2.setVisible(true);
        jTextArea2.setVisible(true);
        jButton4.setVisible(true);
        jLabel7.setVisible(true);
        Police newPolice = Police.searchByID(this.policeID);
        String name = newPolice.name;
        String city = newPolice.city;
        String contact = newPolice.contactNumber;
        String address = newPolice.residenceAddress;
        int casesInvolved = newPolice.casesInvolved;
        jTextField2.setText(city);
        jTextField3.setText(contact);
        jTextField4.setText(name);
        jTextArea2.setText(address);
        jTextField5.setText(""+casesInvolved);
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

