    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String name = jTextField1.getText();
        String contact = jTextField2.getText();
        String aadhar = jTextField3.getText();
        int crimeIndex = jComboBox1.getSelectedIndex();
        String location = jTextField7.getText();
        String victimName = jTextField4.getText();
        String victimContact = jTextField5.getText();
        String changingVar = jTextField6.getText();
        String otherDetails = jTextArea1.getText();
        Person complainant = new Person(name,contact,aadhar);
        int pID = complainant.personID;
        int cID = 0;
        switch(crimeIndex) {
            case 1: Kidnap newKidnap = new Kidnap(otherDetails,changingVar,location);
                    cID = newKidnap.getID();
                    break;
            case 2: Murder newMurder = new Murder(otherDetails,location,changingVar);
                    cID = newMurder.getID();
                    break;
            case 3: Robbery newRobbery = new Robbery(location,otherDetails,changingVar);
                    cID = newRobbery.getID();
                    break;
            case 4: RoadAccident newAccident = new RoadAccident(location,changingVar,otherDetails);
                    cID = newAccident.getID();
                    break;
            case 5: Crime newCrime = new Crime(otherDetails,location);
                    cID = newCrime.getID();
        }
        Person victim = new Person(victimName,victimContact,"");
        int vID = victim.personID;
        Person.markAsWitness(cID, pID);
        Person.markAsVictim(cID, vID);
        Complaint newComp = new Complaint(cID,pID);
        int compID = newComp.complaintID;
        int pass = newComp.password;
        JOptionPane.showMessageDialog(this, "Your complaint is registered. The complaint ID is "+compID+"\nPassword is "+pass);
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

