    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int compID = Integer.parseInt(jTextField1.getText());
        String status = Complaint.getComplaintStatus(compID);
        JOptionPane.showMessageDialog(this, "Crime ID "+compID+" has the status as "+status);// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

